// C# reuses for loop variables, so capture a local variable
var callbacks = new List<Action>();
for (var i = 0; i < 2; i++)
{
    int captured = i;
    callbacks.Add(() => Console.WriteLine("i:{0} capt:{1}",
        i, captured));
}

callbacks[0](); // i:2 capt:0
callbacks[1](); // i:2 capt:1

